	(240,41,'_prize_distribution','field_582775f887aa5'),
	(241,41,'tournament_format','<ul>\r\n 	<li>18 team maximum</li>\r\n 	<li>Maps will be selected by veto</li>\r\n 	<li>Round robin / group play to determine bracket seeding <em>(groups determined based on random shuffle which will be live streamed, bracket seeding based on round differentials from group results)</em></li>\r\n 	<li>Top 2 teams from each group of 6 make it to payoffs 6 team Double elimination bracket for Playoffs <em>(seeded based on group play results)</em></li>\r\n 	<li>Best of 1 for each match</li>\r\n 	<li>Best of 3 for semi-finals</li>\r\n 	<li>Best of 5 for grand finals</li>\r\n</ul>'),
